import BaseCard from "./BaseCard";

interface Horse {
  name: string;
  countryFlag: string;
  imageUrl?: string;
  breed: string;
  age: number;
  sex: "Stallion" | "Gelding" | "Mare" | "Colt" | "Filly";
}

const tagColorMap: Record<Horse["sex"], string> = {
  Stallion: "bg-blue-700",
  Gelding: "bg-blue-400",
  Mare: "bg-pink-600",
  Colt: "bg-purple-500",
  Filly: "bg-purple-300",
};

const HorseCard = ({
  horse,
  onClick,
}: {
  horse: Horse;
  onClick: () => void;
}) => {
  return (
    <BaseCard
      imageUrl={
        horse.imageUrl ||
        "data:image/png;base64,"
      }
      title={`${horse.name} ${horse.countryFlag}`}
      subtitle={`Irk: ${horse.breed}`}
      description={`YaÅŸ: ${horse.age}`}
      tagText={horse.sex}
      tagColor={tagColorMap[horse.sex] || "bg-gray-500"}
      bottomText={horse.countryFlag}
      onClick={onClick}
    />
  );
};

export default HorseCard;
